import request from 'superagent';

import phones from './mockDataPhone';
import categories from './mockCategories';

const BASE = () => 'http://www.mocky.io';

const API = {
  PHONES: `${BASE()}/v2/5da8ad27120000d0d1edb095`,
  CATEGORIES: `${BASE()}/v2/5da8b1521200005400edb0a9`,
};

export const fetchPhones = async () => {
  return new Promise((resolve) => resolve(phones));
};

export const loadMorePhones = async ({ offset }) => {
  return new Promise((resolve) => resolve(phones));
};

export const fetchPhoneById = async (id) => {
  const [phone] = phones.filter((phone) => phone.id === id);
  return new Promise((resolve) => resolve(phone));
};

export const fetchCategories = async () => {
  return new Promise((resolve) => resolve(categories));
};
