import React from 'react';
import { connect } from 'react-redux';
import { getCategories, getActiveCategoryId } from 'selectors';
import { Link, withRouter } from 'react-router-dom';
import { compose } from 'redux';
import cn from 'classnames';
import * as R from 'ramda';

const Categories = ({ categories, activeCategoryId }) => {
  const renderCategory = (category, inx) => {
    const getActiveState = ({ id }) => id === activeCategoryId;
    const linkClass = cn({
      'list-group-item': true,
      'active': getActiveState(category),
    });

    return (
      <Link className={linkClass} key={inx} to={`/categories/${category.id}`}>
        {category.name}
      </Link>
    );
  }

  const renderAllCategory = () => {
    const linkClass = cn({
      'list-group-item': true,
      'active': R.isNil(activeCategoryId),
    });

    return (
      <Link className={linkClass} to="/">
        All
      </Link>
    );
  }

  return (
    <div className="well">
      <h4>Brand</h4>
      <div className="list-group">
        {renderAllCategory()}
        {categories.map((category, inx) => renderCategory(category, inx))}
      </div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  categories: getCategories(state),
  activeCategoryId: getActiveCategoryId(ownProps),
});

export default compose(
  withRouter,
  connect(mapStateToProps, null),
)(Categories);
