import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import * as R from 'ramda';

import { removePhoneFromBasket, cleanBasket, basketCheckout } from 'actions';
import { getTotalBasketPrice, getBasketPhonesWithCount } from 'selectors';

const Basket = ({
  phones,
  total,
  removePhoneFromBasket,
  cleanBasket,
  basketCheckout,
}) => {
  const isBasketEmpty = R.isEmpty(phones);

  const renderContent = () => (
    <div>
      {isBasketEmpty && <div>Your shopping cart is empty!</div>}
      <div className="table-responsive">
        <table className="table-bordered table-striped table-condensed cf">
          <tbody>
            {phones.map((phone, inx) => (
              <tr className="item-checout" key={inx}>
                <td className="first-column-checkout">
                  <img className="img-thumbnail" src={phone.image} alt={phone.name} />
                </td>
                <td>{phone.name}</td>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span
                    onClick={() => removePhoneFromBasket({ id: phone.id })}
                    className="delete-cart"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {R.not(isBasketEmpty) &&
        <div className="row">
          <div className="pull-right total-user-checkout">
            <b>${total}</b>
          </div>
        </div>
      }
    </div>
  );

  const renderSidebar = () => (
    <div>
      <Link to="/" className="btn btn-info">
        <span className="glyphicon glyphicon-shopping-cart" />
        <span>Continue shopping!</span>
      </Link>
      {R.not(isBasketEmpty) &&
      <div>
        <button className="btn btn-danger" onClick={cleanBasket}>
          <span className="glyphicon glyphicon-trash" />
           Clean cart
        </button>
        <button
          className="btn btn-success"
          onClick={() => basketCheckout({ phones })}
        >
          <span className="glyphicon glyphicon-envelope" />
          Checkout
        </button>
      </div>
      }
    </div>
  );

  return (
    <div className="view-container">
      <div className="container">
        <div className="row">
          <div className="col-md-9">{renderContent()}</div>
          <div className="col-md-3 btn-user-checkout">{renderSidebar()}</div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  phones: getBasketPhonesWithCount(state),
  total: getTotalBasketPrice(state),
});

const createAction = { removePhoneFromBasket, cleanBasket, basketCheckout };

export default connect(mapStateToProps, createAction)(Basket);
