import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import * as R from 'ramda';

import { loadMorePhones, addPhoneToBasket, fetchCategories, fetchPhones } from 'actions';
import { phoneSelector } from 'selectors';
import Layout from 'containers/layout';


class Phones extends Component {
  componentDidMount() {
    const { fetchCategories, fetchPhones } = this.props;
    fetchPhones();
    fetchCategories();
  }

  renderPhone = (phone, inx) => {
    const shortDescription = `${R.take(60, phone.description)}...`;
    const { addPhoneToBasket } = this.props;

    return (
      <div className="col-sm-4 col-lg-4 col-md-4 book-list" key={inx}>
        <div className="thumbnail">
          <img className="img-thumbnail" src={phone.image} alt={phone.name} />
          <div className="caption">
            <h4 className="pull-right">
              $
              {phone.price}
            </h4>
            <h4>
              <Link to={`/phones/${phone.id}`}>{phone.name}</Link>
            </h4>
            <p>{shortDescription}</p>
            <p className="itemButton">
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => addPhoneToBasket(phone.id)}
              >
                By now!
              </button>
              <Link to={`/phones/${phone.id}`} className="btn btn-default">More info</Link>
            </p>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { phones, loadMorePhones } = this.props;

    return (
      <Layout>
        <div className="books row">
          {phones.map((phone, inx) => this.renderPhone(phone, inx))}
        </div>
        <div className="row">
          <div className="col-md-12">
            <button
              type="button"
              className="pull-right btn btn-primary"
              onClick={loadMorePhones}
            >
              Load More
            </button>
          </div>
        </div>
      </Layout>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  phones: phoneSelector(state, ownProps),
});

const actionCreators = {
  loadMorePhones,
  addPhoneToBasket,
  fetchPhones,
  fetchCategories,
};

export default connect(mapStateToProps, actionCreators)(Phones);
