import { handleActions } from 'redux-actions';
import * as actions from 'actions';

const initialState = [];

export default handleActions({
  [actions.addPhonesToBasket]: (state, { payload: { id } }) => [...state, id],
  [actions.removePhoneFromBasket]: (state, { payload: { id } }) => state.filter((basketId) => basketId !== id),
  [actions.cleanBasket]: () => initialState,
  [actions.basketCheckout]: (state, { payload: { phones } }) => {
    alert(JSON.stringify(phones));
    return state;
  },
}, initialState);
